// import { moduleMetadata, type Meta, type StoryObj } from '@storybook/angular';
// // import { PivotheadAngularModule } from './pivothead-angular.module';
// import { PivotHeadWrapperComponent } from './pivothead-wrapper.component';
// import { sampleData, sampleOptions } from '../../../../examples/pivot-head-angular-demo/src/app/sample-data';
import { PivotHeadWrapperComponent } from './pivothead-wrapper.component';
import { applicationConfig } from '@storybook/angular';
import { provideAnimations } from '@angular/platform-browser/animations';
// Common args for all stories
const commonArgs = {
    data: [
        { id: 1, name: 'John Doe', department: 'Engineering', salary: 85000, hireDate: '2020-01-15' },
        { id: 2, name: 'Jane Smith', department: 'Marketing', salary: 78000, hireDate: '2019-05-22' },
        { id: 3, name: 'Mike Johnson', department: 'Engineering', salary: 92000, hireDate: '2018-11-03' },
        { id: 4, name: 'Sarah Williams', department: 'HR', salary: 68000, hireDate: '2021-03-10' },
        { id: 5, name: 'David Brown', department: 'Marketing', salary: 75000, hireDate: '2020-07-18' },
    ],
    options: {
        columns: [
            { field: 'id', header: 'ID', width: '80px' },
            { field: 'name', header: 'Name', sortable: true },
            { field: 'department', header: 'Department', filter: true },
            { field: 'salary', header: 'Salary', type: 'currency', format: { currency: 'USD' } },
            { field: 'hireDate', header: 'Hire Date', type: 'date' },
        ],
        rowSelection: 'multiple',
        pagination: true,
        pageSize: 10,
        showToolbar: true,
    },
    mode: 'default',
};
const meta = {
    title: 'Components/PivotHeadWrapper',
    component: PivotHeadWrapperComponent,
    tags: ['autodocs'],
    decorators: [
        applicationConfig({
            providers: [
                provideAnimations(),
            ],
        }),
    ],
    args: commonArgs,
    render: (args) => ({
        props: {
            data: args['data'],
            options: args['options'],
            filters: args['filters'],
            pagination: args['pagination'],
            mode: args['mode'],
            onStateChange: (event) => console.log('State changed:', event.detail),
            onViewModeChange: (event) => console.log('View mode changed:', event.detail),
            onPaginationChange: (event) => console.log('Pagination changed:', event.detail),
        },
        template: `
      <div style="height: 600px; width: 100%;">
        <pivot-head-wrapper
          [data]="data"
          [options]="options"
          [filters]="filters"
          [pagination]="pagination"
          [mode]="mode"
          (stateChange)="onStateChange($event)"
          (viewModeChange)="onViewModeChange($event)"
          (paginationChange)="onPaginationChange($event)"
        >
          <div slot="header">
            <h2>Custom Header Content</h2>
            <p>This is a custom header that appears in minimal mode</p>
          </div>
          <div slot="body">
            <p>Custom body content can go here in minimal mode</p>
          </div>
        </pivot-head-wrapper>
      </div>
    `,
    }),
    argTypes: {
        mode: {
            control: 'select',
            options: ['default', 'minimal', 'none'],
            description: 'Display mode of the pivot table',
        },
    },
};
export default meta;
export const Default = {
    args: {
        ...commonArgs
    }
};
export const WithGrouping = {
    args: {
        ...commonArgs,
        options: {
            ...commonArgs.options,
            groupConfig: {
                rowFields: ['department'],
                columnFields: [],
                grouper: (item, fields) => fields.map(field => item[field]).join('-'),
            },
        },
    },
};
export const WithFilters = {
    args: {
        ...commonArgs,
        filters: [
            { field: 'department', operator: 'equals', value: 'Engineering' },
            { field: 'salary', operator: 'greaterThan', value: 80000 },
        ],
    },
};
export const MinimalMode = {
    args: {
        ...commonArgs,
        mode: 'minimal',
    },
};
export const WithPagination = {
    args: {
        ...commonArgs,
        pagination: {
            pageSize: 2,
            currentPage: 1,
            totalPages: 3,
        },
    },
};
// This story demonstrates how to use the component methods
export const WithMethods = {
    args: {
        ...commonArgs,
    },
    render: (args) => ({
        props: {
            data: args['data'],
            options: args['options'],
            filters: args['filters'],
            pagination: args['pagination'],
            mode: args['mode'],
            onRefresh: () => {
                const pivotTable = document.querySelector('pivot-head-wrapper');
                if (pivotTable) {
                    pivotTable.refresh();
                    console.log('Table refreshed');
                }
            },
            onExportToExcel: () => {
                const pivotTable = document.querySelector('pivot-head-wrapper');
                if (pivotTable) {
                    pivotTable.exportToExcel('pivot-data');
                }
            },
        },
        template: `
      <div>
        <div style="margin-bottom: 1rem; display: flex; gap: 0.5rem;">
          <button (click)="onRefresh()" style="padding: 0.5rem 1rem; background: #4CAF50; color: white; border: none; border-radius: 4px; cursor: pointer;">
            Refresh Table
          </button>
          <button (click)="onExportToExcel()" style="padding: 0.5rem 1rem; background: #2196F3; color: white; border: none; border-radius: 4px; cursor: pointer;">
            Export to Excel
          </button>
        </div>
        <div style="height: 500px; width: 100%;">
          <pivot-head-wrapper
            [data]="data"
            [options]="options"
            [mode]="mode"
          ></pivot-head-wrapper>
        </div>
      </div>
    `,
    }),
};
