    title: 'PivotHead/PivotHeadWrapperComponent',
    component: PivotHeadWrapperComponent,
    // This 'decorators' section is THE MOST IMPORTANT PART for Angular
    decorators: [
        moduleMetadata({
            imports: [
                PivotHeadWrapperComponent, // <-- For standalone, you import the component itself
                CommonModule // <-- Also import any modules it depends on (e.g., for *ngIf, *ngFor)
            ],
        }),
    ],
    tags: ['autodocs'],
};
export default meta;
// This is the "Default" story that will be displayed
export const Default = {
    args: {
        // Pass the data to the component's @Input() properties
        data: sampleData,
        options: sampleOptions,
    },
};
